class Solution {
public:
    string largestNumber(vector<int>& nums) {
        sort(nums.begin(),nums.end(),[](int a,int b){
            return to_string(a)+to_string(b)>to_string(b)+to_string(a);
        });
        string rst;
        for(int a:nums){
            rst+=to_string(a);
        }
        if(rst[0]=='0')return "0";
        return rst;
        
    }
};
